function appendTasks(){$("#someContainer").empty();for(var a=0;a<abilityData.length;a++){$("#someContainer").append("<div class='skillsClass'></div>");var b=$("#someContainer").children().last();b.data("name",abilityData[a].name),b.append("<p class='lead'>"+abilityData[a].name+"</p>")}}var abilityData=[];$(document).ready(function(){$("#skillsForm").submit(function(a){a.preventDefault();var b=$("#skillsForm").serialize();$.ajax({type:"POST",data:b,url:"api/addSkills",success:function(a){abilityData=a,appendTasks()}})})});